import React, { useEffect, useRef, useState } from 'react';
import ReactDOM from 'react-dom';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
import { VRM, VRMSchema, VRMUtils } from '@pixiv/three-vrm';
import {drawConnectors,drawLandmarks} from '@mediapipe/drawing_utils'
import { Holistic, POSE_CONNECTIONS, HAND_CONNECTIONS, FACEMESH_TESSELATION} from '@mediapipe/holistic';
import { Face, Pose, Hand, Utils, Vector } from 'kalidokit';
import * as CameraUtils from '@mediapipe/camera_utils';

const clamp = Utils.clamp;
const lerp = Vector.lerp;

const App = () => {
  const rendererRef = useRef();
  const canvasRef = useRef();
  const cameraRef = useRef();
  const controlsRef = useRef();
  const sceneRef = useRef();
  const currentVrmRef = useRef();
  const [isCameraOn, setCameraOn] = useState(true);
  const toggleCamera = () => {
    setCameraOn(!isCameraOn);
  };

  const loader = new GLTFLoader();
    loader.crossOrigin = 'anonymous';
    loader.load(
      'https://rolling-filters.s3.amazonaws.com/3d/vroid.vrm',
      (gltf) => {
        VRMUtils.removeUnnecessaryJoints(gltf.scene);

        VRM.from(gltf).then((vrm) => {
          scene.add(vrm.scene);
          currentVrmRef.current = vrm;
          currentVrmRef.current.scene.rotation.y = Math.PI;
        });
      },
      (progress) => {
        console.log('Loading model...', 100.0 * (progress.loaded / progress.total), '%');
      },
      (error) => {
        console.error(error);
      }
    );

  useEffect(() => {
    const videoElement = document.querySelector(".input_video");
    const guideCanvas = document.querySelector('canvas.guides');

    const renderer = new THREE.WebGLRenderer({ alpha: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    rendererRef.current = renderer;
    document.body.appendChild(renderer.domElement);

    const orbitCamera = new THREE.PerspectiveCamera(70, window.innerWidth / window.innerHeight, 0.1, 1000);
    orbitCamera.position.set(0.0, 1.4, 0.7);
    cameraRef.current = orbitCamera;

    const orbitControls = new OrbitControls(orbitCamera, renderer.domElement);
    orbitControls.screenSpacePanning = true;
    orbitControls.target.set(0.0, 1.4, 0.0);
    orbitControls.update();
    controlsRef.current = orbitControls;

    sceneRef.current = scene;

    const light = new THREE.DirectionalLight(0xffffff);
    light.position.set(1.0, 1.0, 1.0).normalize();
    scene.add(light);

    const clock = new THREE.Clock();

    function animate() {
      requestAnimationFrame(animate);

      if (currentVrmRef.current) {
        currentVrmRef.current.update(clock.getDelta());
      }
      renderer.render(scene, orbitCamera);
    }
    animate();



    const rigRotation = (name, rotation = { x: 0, y: 0, z: 0 }, dampener = 1, lerpAmount = 0.3) => {
      if (!currentVrmRef.current) {
        return;
      }
      const Part = currentVrmRef.current.humanoid.getBoneNode(VRMSchema.HumanoidBoneName[name]);
      if (!Part) {
        return;
      }

      const euler = new THREE.Euler(rotation.x * dampener, rotation.y * dampener, rotation.z * dampener);
      const quaternion = new THREE.Quaternion().setFromEuler(euler);
      Part.quaternion.slerp(quaternion, lerpAmount);
    };



    const rigFace = (riggedFace) => {
      if (!currentVrmRef.current) {
        return;
      }
      rigRotation('Neck', riggedFace.head, 0.7);

      const Blendshape = currentVrmRef.current.blendShapeProxy;
      const PresetName = VRMSchema.BlendShapePresetName;

      riggedFace.eye.l = lerp(clamp(1 - riggedFace.eye.l, 0, 1), Blendshape.getValue(PresetName.Blink), 0.5);
      riggedFace.eye.r = lerp(clamp(1 - riggedFace.eye.r, 0, 1), Blendshape.getValue(PresetName.Blink), 0.5);
      riggedFace.eye = Face.stabilizeBlink(riggedFace.eye, riggedFace.head.y);
      Blendshape.setValue(PresetName.Blink, riggedFace.eye.l);

      Blendshape.setValue(PresetName.I, lerp(riggedFace.mouth.shape.I, Blendshape.getValue(PresetName.I), 0.5));
      Blendshape.setValue(PresetName.A, lerp(riggedFace.mouth.shape.A, Blendshape.getValue(PresetName.A), 0.5));
      Blendshape.setValue(PresetName.E, lerp(riggedFace.mouth.shape.E, Blendshape.getValue(PresetName.E), 0.5));
      Blendshape.setValue(PresetName.O, lerp(riggedFace.mouth.shape.O, Blendshape.getValue(PresetName.O), 0.5));
      Blendshape.setValue(PresetName.U, lerp(riggedFace.mouth.shape.U, Blendshape.getValue(PresetName.U), 0.5));

      let lookTarget = new THREE.Euler(
        lerp(oldLookTarget.x, riggedFace.pupil.y, 0.4),
        lerp(oldLookTarget.y, riggedFace.pupil.x, 0.4),
        0,
        'XYZ'
      );
      oldLookTarget.copy(lookTarget);
      currentVrmRef.current.lookAt.applyer.lookAt(lookTarget);
    };

    const animateVRM = (vrm, results) => {
      if (!vrm) {
        return;
      }
      let riggedFace;

      const faceLandmarks = results.faceLandmarks;

      if (faceLandmarks) {
        riggedFace = Face.solve(faceLandmarks, {
          runtime: 'mediapipe',
          video: videoElement,
        });
        rigFace(riggedFace);
      }
    };

    let oldLookTarget = new THREE.Euler();
    const onResults = (results) => {
      drawResults(results);
      animateVRM(currentVrmRef.current, results);
    };

    const drawResults = (results) => {
      guideCanvas.width = videoElement.videoWidth;
      guideCanvas.height = videoElement.videoHeight;
      const canvasCtx = guideCanvas.getContext('2d');
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, guideCanvas.width, guideCanvas.height);
      drawConnectors(canvasCtx, results.faceLandmarks, FACEMESH_TESSELATION, {
        color: '#C0C0C070',
        lineWidth: 1,
      });
      if (results.faceLandmarks && results.faceLandmarks.length === 478) {
        drawLandmarks(canvasCtx, [results.faceLandmarks[468], results.faceLandmarks[468 + 5]], {
          color: '#ffe603',
          lineWidth: 2,
        });
      }
    };

    const holistic = new Holistic({
      locateFile: (file) => {
        return `https://cdn.jsdelivr.net/npm/@mediapipe/holistic@0.5.1635989137/${file}`;
      },
    },);

    holistic.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      minDetectionConfidence: 0.7,
      minTrackingConfidence: 0.7,
      refineFaceLandmarks: true,
    });

    holistic.onResults(onResults);


    const camera = new CameraUtils.Camera(videoElement, {
      onFrame: async () => {
        await holistic.send({ image: videoElement });
      },
      width: 640,
      height: 480
    });

    if (isCameraOn) {
      camera.start();
    } else {
      camera.stop();
    }

    return () => {
      camera.stop();
    };
  }, [isCameraOn]);

  return (
    <div>
      <button onClick={toggleCamera} style={{ position: 'absolute', top: '10px', right: '10px' }}>
        {isCameraOn ? 'Turn camera off' : 'Turn camera on'}
      </button>
      <video className="input_video" ref={canvasRef} />
      <canvas className="guides" />
    </div>
  );
};
ReactDOM.render(<App />, document.getElementById('root'));
