import React, { useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
import { VRM, VRMSchema, VRMUtils } from '@pixiv/three-vrm';
import { Holistic, drawLandmarks, drawConnectors, POSE_CONNECTIONS, HAND_CONNECTIONS, FACEMESH_TESSELATION} from '@mediapipe/holistic';
import { Face, Pose, Hand, Utils, Vector } from 'kalidokit';
import * as CameraUtils from '@mediapipe/camera_utils';

//const remap = Utils.remap;
const clamp = Utils.clamp;
const lerp = Vector.lerp;

const App = () => {
  const rendererRef = useRef();
  const canvasRef = useRef();
  const cameraRef = useRef();
  const controlsRef = useRef();
  const sceneRef = useRef();
  const currentVrmRef = useRef();

  useEffect(() => {
    const videoElement = document.querySelector(".input_video");
    const guideCanvas = document.querySelector('canvas.guides');

    const renderer = new THREE.WebGLRenderer({ alpha: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    rendererRef.current = renderer;
    document.body.appendChild(renderer.domElement);

    const orbitCamera = new THREE.PerspectiveCamera(35, window.innerWidth / window.innerHeight, 0.1, 1000);
    orbitCamera.position.set(0.0, 1.4, 0.7);
    cameraRef.current = orbitCamera;

    const orbitControls = new OrbitControls(orbitCamera, renderer.domElement);
    orbitControls.screenSpacePanning = true;
    orbitControls.target.set(0.0, 1.4, 0.0);
    orbitControls.update();
    controlsRef.current = orbitControls;

    const scene = new THREE.Scene();
    sceneRef.current = scene;

    const light = new THREE.DirectionalLight(0xffffff);
    light.position.set(1.0, 1.0, 1.0).normalize();
    scene.add(light);

    const clock = new THREE.Clock();

    function animate() {
      requestAnimationFrame(animate);

      if (currentVrmRef.current) {
        currentVrmRef.current.update(clock.getDelta());
      }
      renderer.render(scene, orbitCamera);
    }
    animate();

    const loader = new GLTFLoader();
    loader.crossOrigin = 'anonymous';
    loader.load(
      'https://rolling-filters.s3.amazonaws.com/3d/vroid.vrm',
      (gltf) => {
        VRMUtils.removeUnnecessaryJoints(gltf.scene);

        VRM.from(gltf).then((vrm) => {
          scene.add(vrm.scene);
          currentVrmRef.current = vrm;
          currentVrmRef.current.scene.rotation.y = Math.PI;
        });
      },
      (progress) => {
        console.log('Loading model...', 100.0 * (progress.loaded / progress.total), '%');
      },
      (error) => {
        console.error(error);
      }
    );

    const rigRotation = (name, rotation = { x: 0, y: 0, z: 0 }, dampener = 1, lerpAmount = 0.3) => {
      if (!currentVrmRef.current) {
        return;
      }
      const Part = currentVrmRef.current.humanoid.getBoneNode(VRMSchema.HumanoidBoneName[name]);
      if (!Part) {
        return;
      }

      const euler = new THREE.Euler(rotation.x * dampener, rotation.y * dampener, rotation.z * dampener);
      const quaternion = new THREE.Quaternion().setFromEuler(euler);
      Part.quaternion.slerp(quaternion, lerpAmount);
    };

    const rigPosition = (name, position = { x: 0, y: 0, z: 0 }, dampener = 1, lerpAmount = 0.3) => {
      if (!currentVrmRef.current) {
        return;
      }
      const Part = currentVrmRef.current.humanoid.getBoneNode(VRMSchema.HumanoidBoneName[name]);
      if (!Part) {
        return;
      }

      const vector = new THREE.Vector3(position.x * dampener, position.y * dampener, position.z * dampener);
      Part.position.lerp(vector, lerpAmount);
    };

    const rigFace = (riggedFace) => {
      if (!currentVrmRef.current) {
        return;
      }
      rigRotation('Neck', riggedFace.head, 0.7);

      const Blendshape = currentVrmRef.current.blendShapeProxy;
      const PresetName = VRMSchema.BlendShapePresetName;

      riggedFace.eye.l = lerp(clamp(1 - riggedFace.eye.l, 0, 1), Blendshape.getValue(PresetName.Blink), 0.5);
      riggedFace.eye.r = lerp(clamp(1 - riggedFace.eye.r, 0, 1), Blendshape.getValue(PresetName.Blink), 0.5);
      riggedFace.eye = Face.stabilizeBlink(riggedFace.eye, riggedFace.head.y);
      Blendshape.setValue(PresetName.Blink, riggedFace.eye.l);

      Blendshape.setValue(PresetName.I, lerp(riggedFace.mouth.shape.I, Blendshape.getValue(PresetName.I), 0.5));
      Blendshape.setValue(PresetName.A, lerp(riggedFace.mouth.shape.A, Blendshape.getValue(PresetName.A), 0.5));
      Blendshape.setValue(PresetName.E, lerp(riggedFace.mouth.shape.E, Blendshape.getValue(PresetName.E), 0.5));
      Blendshape.setValue(PresetName.O, lerp(riggedFace.mouth.shape.O, Blendshape.getValue(PresetName.O), 0.5));
      Blendshape.setValue(PresetName.U, lerp(riggedFace.mouth.shape.U, Blendshape.getValue(PresetName.U), 0.5));

      let lookTarget = new THREE.Euler(
        lerp(oldLookTarget.x, riggedFace.pupil.y, 0.4),
        lerp(oldLookTarget.y, riggedFace.pupil.x, 0.4),
        0,
        'XYZ'
      );
      oldLookTarget.copy(lookTarget);
      currentVrmRef.current.lookAt.applyer.lookAt(lookTarget);
    };

    const animateVRM = (vrm, results) => {
      if (!vrm) {
        return;
      }
      let riggedPose, riggedLeftHand, riggedRightHand, riggedFace;

      const faceLandmarks = results.faceLandmarks;
      const pose3DLandmarks = results.ea;
      const pose2DLandmarks = results.poseLandmarks;
      const leftHandLandmarks = results.rightHandLandmarks;
      const rightHandLandmarks = results.leftHandLandmarks;

      if (faceLandmarks) {
        riggedFace = Face.solve(faceLandmarks, {
          runtime: 'mediapipe',
          video: videoElement,
        });
        rigFace(riggedFace);
      }

      if (pose2DLandmarks && pose3DLandmarks) {
        riggedPose = Pose.solve(pose3DLandmarks, pose2DLandmarks, {
          runtime: 'mediapipe',
          video: videoElement,
        });
        rigRotation('Hips', riggedPose.Hips.rotation, 0.7);
        rigPosition('Hips', {
          x: -riggedPose.Hips.position.x,
          y: riggedPose.Hips.position.y + 1,
          z: -riggedPose.Hips.position.z,
        });
        rigRotation('Chest', riggedPose.Spine, 0.25, 0.3);
        rigRotation('Spine', riggedPose.Spine, 0.45, 0.3);
        rigRotation('RightUpperArm', riggedPose.RightUpperArm, 1, 0.3);
        rigRotation('RightLowerArm', riggedPose.RightLowerArm, 1, 0.3);
        rigRotation('LeftUpperArm', riggedPose.LeftUpperArm, 1, 0.3);
        rigRotation('LeftLowerArm', riggedPose.LeftLowerArm, 1, 0.3);
        rigRotation('LeftUpperLeg', riggedPose.LeftUpperLeg, 1, 0.3);
        rigRotation('LeftLowerLeg', riggedPose.LeftLowerLeg, 1, 0.3);
        rigRotation('RightUpperLeg', riggedPose.RightUpperLeg, 1, 0.3);
        rigRotation('RightLowerLeg', riggedPose.RightLowerLeg, 1, 0.3);
      }

      if (leftHandLandmarks) {
        riggedLeftHand = Hand.solve(leftHandLandmarks, 'Left');
        rigRotation('LeftHand', {
          z: riggedPose.LeftHand.z,
          y: riggedLeftHand.LeftWrist.y,
          x: riggedLeftHand.LeftWrist.x,
        });
        rigRotation('LeftRingProximal', riggedLeftHand.LeftRingProximal);
        rigRotation('LeftRingIntermediate', riggedLeftHand.LeftRingIntermediate);
        rigRotation('LeftRingDistal', riggedLeftHand.LeftRingDistal);
        rigRotation('LeftIndexProximal', riggedLeftHand.LeftIndexProximal);
        rigRotation('LeftIndexIntermediate', riggedLeftHand.LeftIndexIntermediate);
        rigRotation('LeftIndexDistal', riggedLeftHand.LeftIndexDistal);
        rigRotation('LeftMiddleProximal', riggedLeftHand.LeftMiddleProximal);
        rigRotation('LeftMiddleIntermediate', riggedLeftHand.LeftMiddleIntermediate);
        rigRotation('LeftMiddleDistal', riggedLeftHand.LeftMiddleDistal);
        rigRotation('LeftThumbProximal', riggedLeftHand.LeftThumbProximal);
        rigRotation('LeftThumbIntermediate', riggedLeftHand.LeftThumbIntermediate);
        rigRotation('LeftThumbDistal', riggedLeftHand.LeftThumbDistal);
        rigRotation('LeftLittleProximal', riggedLeftHand.LeftLittleProximal);
        rigRotation('LeftLittleIntermediate', riggedLeftHand.LeftLittleIntermediate);
        rigRotation('LeftLittleDistal', riggedLeftHand.LeftLittleDistal);
      }
      if (rightHandLandmarks) {
        riggedRightHand = Hand.solve(rightHandLandmarks, 'Right');
        rigRotation('RightHand', {
          z: riggedPose.RightHand.z,
          y: riggedRightHand.RightWrist.y,
          x: riggedRightHand.RightWrist.x,
        });
        rigRotation('RightRingProximal', riggedRightHand.RightRingProximal);
        rigRotation('RightRingIntermediate', riggedRightHand.RightRingIntermediate);
        rigRotation('RightRingDistal', riggedRightHand.RightRingDistal);
        rigRotation('RightIndexProximal', riggedRightHand.RightIndexProximal);
        rigRotation('RightIndexIntermediate', riggedRightHand.RightIndexIntermediate);
        rigRotation('RightIndexDistal', riggedRightHand.RightIndexDistal);
        rigRotation('RightMiddleProximal', riggedRightHand.RightMiddleProximal);
        rigRotation('RightMiddleIntermediate', riggedRightHand.RightMiddleIntermediate);
        rigRotation('RightMiddleDistal', riggedRightHand.RightMiddleDistal);
        rigRotation('RightThumbProximal', riggedRightHand.RightThumbProximal);
        rigRotation('RightThumbIntermediate', riggedRightHand.RightThumbIntermediate);
        rigRotation('RightThumbDistal', riggedRightHand.RightThumbDistal);
        rigRotation('RightLittleProximal', riggedRightHand.RightLittleProximal);
        rigRotation('RightLittleIntermediate', riggedRightHand.RightLittleIntermediate);
        rigRotation('RightLittleDistal', riggedRightHand.RightLittleDistal);
      }
    };

    let oldLookTarget = new THREE.Euler();
    const onResults = (results) => {
      drawResults(results);
      animateVRM(currentVrmRef.current, results);
    };

    const drawResults = (results) => {
      guideCanvas.width = videoElement.videoWidth;
      guideCanvas.height = videoElement.videoHeight;
      const canvasCtx = guideCanvas.getContext('2d');
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, guideCanvas.width, guideCanvas.height);
      drawConnectors(canvasCtx, results.poseLandmarks, POSE_CONNECTIONS, {
        color: '#00cff7',
        lineWidth: 4,
      });
      drawLandmarks(canvasCtx, results.poseLandmarks, {
        color: '#ff0364',
        lineWidth: 2,
      });
      drawConnectors(canvasCtx, results.faceLandmarks, FACEMESH_TESSELATION, {
        color: '#C0C0C070',
        lineWidth: 1,
      });
      if (results.faceLandmarks && results.faceLandmarks.length === 478) {
        drawLandmarks(canvasCtx, [results.faceLandmarks[468], results.faceLandmarks[468 + 5]], {
          color: '#ffe603',
          lineWidth: 2,
        });
      }
      drawConnectors(canvasCtx, results.leftHandLandmarks, HAND_CONNECTIONS, {
        color: '#eb1064',
        lineWidth: 5,
      });
      drawLandmarks(canvasCtx, results.leftHandLandmarks, {
        color: '#00cff7',
        lineWidth: 2,
      });
      drawConnectors(canvasCtx, results.rightHandLandmarks, HAND_CONNECTIONS, {
        color: '#22c3e3',
        lineWidth: 5,
      });
      drawLandmarks(canvasCtx, results.rightHandLandmarks, {
        color: '#ff0364',
        lineWidth: 2,
      });
    };

    const holistic = new Holistic({
      locateFile: (file) => {
        return `https://cdn.jsdelivr.net/npm/@mediapipe/holistic@0.5.1635989137/${file}`;
      },
    },);

    holistic.setOptions({
      modelComplexity: 1,
      smoothLandmarks: true,
      minDetectionConfidence: 0.7,
      minTrackingConfidence: 0.7,
      refineFaceLandmarks: true,
    });

    holistic.onResults(onResults);

    const drawGuides = (results) => {
      guideCanvas.width = videoElement.videoWidth;
      guideCanvas.height = videoElement.videoHeight;
      const canvasCtx = guideCanvas.getContext('2d');
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, guideCanvas.width, guideCanvas.height);
      drawConnectors(canvasCtx, results.poseLandmarks, POSE_CONNECTIONS, {
        color: '#00cff7',
        lineWidth: 4,
      });
      drawLandmarks(canvasCtx, results.poseLandmarks, {
        color: '#ff0364',
        lineWidth: 2,
      });
      drawConnectors(canvasCtx, results.faceLandmarks, FACEMESH_TESSELATION, {
        color: '#C0C0C070',
        lineWidth: 1,
      });
      if (results.faceLandmarks && results.faceLandmarks.length === 478) {
        drawLandmarks(canvasCtx, [results.faceLandmarks[468], results.faceLandmarks[468 + 5]], {
          color: '#ffe603',
          lineWidth: 2,
        });
      }
      drawConnectors(canvasCtx, results.leftHandLandmarks, HAND_CONNECTIONS, {
        color: '#eb1064',
        lineWidth: 5,
      });
      drawLandmarks(canvasCtx, results.leftHandLandmarks, {
        color: '#00cff7',
        lineWidth: 2,
      });
      drawConnectors(canvasCtx, results.rightHandLandmarks, HAND_CONNECTIONS, {
        color: '#22c3e3',
        lineWidth: 5,
      });
      drawLandmarks(canvasCtx, results.rightHandLandmarks, {
        color: '#ff0364',
        lineWidth: 2,
      });
    };
    const camera = new CameraUtils.Camera(videoElement, {
      onFrame: async () => {
        await holistic.send({ image: videoElement });
      },
      width: 640,
      height: 480
    });

    camera.start();

    return () => {
      camera.stop();
    };
  }, []);

  return (
    <div>
      <video className="input_video" ref={canvasRef} />
      <canvas className="guides" />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
